	}

	.nav-links:not(.quick-links) li:not(.md-header-toolbar) a:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) .participants-more .btn-link:hover, .participants-more .nav-links:not(.quick-links) li:not(.md-header-toolbar) .btn-link:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) .user-list-more .btn-link:hover, .user-list-more .nav-links:not(.quick-links) li:not(.md-header-toolbar) .btn-link:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) a:active, .nav-links:not(.quick-links) li:not(.md-header-toolbar) a:focus, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:active, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:focus {
		border-color: #2a66aa;
		color: var(--lighter-text-color-3);
	}

	.login-page .form-control:active, .login-page .search form:active, .search .login-page form:active, .login-page .form-control:focus, .login-page .common-note-form .md-area.is-focused, .common-note-form .login-page .md-area.is-focused, .login-page .search form:focus, .search .login-page form:focus, .login-page .search.search-active form, .search.search-active .login-page form {
		background-color: #313541;
	}

	.form-control:focus, .common-note-form .md-area.is-focused, .search form:focus, .search.search-active form {
		color: var(--main-text-color);
	}

	.gl-show-field-errors .gl-field-hint {
		color: #d3d3d3;;
	}

	/* ===== Authorize external website/application ===== */

	.modal-content {
		background-color: var(--main-background);
	}

	.modal-header {
		background-color: var(--main-background);
		border-color: var(--light-divider);
	}

	.modal-body {
		background-color: var(--main-background);
	}

	.form-actions {
		background-color: var(--main-background);
		border-color: var(--light-divider);
	}

	.modal-doorkeepr-auth .scope-description, .doorkeeper-app-form .scope-description {
