	}

	.dropdown-menu-inner-title {
		color: #cbcbcb;
	}

	.dropdown-menu-inner-content {
		color: #bababa;
	}

	.git-clone-holder .form-control, .git-clone-holder .search form, .search .git-clone-holder form {
